		[Test]
		public void TestTPLChain()
		{
			var task1 = new Task(() =>
			{
				Thread.Sleep(100);
				Console.WriteLine(1);
			});
			var task2 = task1.ContinueWith(_ =>
			{
				Console.WriteLine(2);
			});
			task1.Start();
			task2.Wait();
		}

